const int maxn = 3e5+5;
pii girl[maxn];
string cute;

int bfs(int x) {
  int ret = 1e9;
  if (girl[x].fi != 0) {
    ret = min(ret, bfs(girl[x].fi)+(cute[x-1]!='L'));
  }
  if (girl[x].se != 0) {
    ret = min(ret, bfs(girl[x].se)+(cute[x-1]!='R'));
  }
  if (ret == 1e9) return 0;
  return ret;
}

